    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(tfNama.getText().equals("")||tfBB.getText().equals("")||tfTinggi.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Data Harus Diisi");
        }else if(rbKelaminPerempuan.isSelected()||rbKelaminLaki.isSelected()){
        try{
            Proses();
        }catch(Exception e){
                JOptionPane.showMessageDialog(null, "Inputan Harus Diisi");
        }
        }else{
            JOptionPane.showMessageDialog(null, "Radio Button Belum Diisi");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

