    private void Proses() {
        ti = Double.parseDouble(tfTinggi.getText());
        bt = Double.parseDouble(tfBB.getText());
        
        if(rbKelaminPerempuan.isSelected()){
            h1 = (ti - 100) * 1;
        }
        if(rbKelaminLaki.isSelected()){
            h1 = (ti - 104) * 1;
        }
        
        temp = Double.toString(h1.intValue());
        
        if(h1<bt){
            ideal.setText(temp);
            hasil.setText("Maaf "+tfNama.getText()+" , Sepertinya Anda OverWeight :( ");
            saran.setText("Banyaklah Berolahraga dan Makan Makanan Yang Sehat");
        }else if(h1>bt){
            ideal.setText(temp);
            hasil.setText("Maaf "+tfNama.getText()+" , Sepertinya Anda UnderWeight :( ");
            saran.setText("Banyaklah Makan Yang Berkarbohidrat Tinggi Dan Sehat");
        }else{
            ideal.setText(temp);
            hasil.setText("Hello "+tfNama.getText()+" , Berat Badan Anda Sudah Ideal ");
            saran.setText("Pertahankan Dan Jangan Sampai Over Atau UnderWeight");
        }
    }
